import React, {useContext} from 'react'
import CartContext from './CartContext'
import CartItem  from './CartItem'
import "./carrito.css"


const Carrito = () => {

    const { cart, total } = useContext(CartContext)

    return (
        <>
            <h1 className="cartTitle">Tu carrito</h1>
            <div className="carritoBox">
            {/* {cart.length ?  */}
                {/* {cart.map((carrito => [
                    <CartItem key={carrito.id} item={carrito.item} cantidad={carrito.cantidad}/>
                ]))
                } */}

                { cart.length > 0 && cart.map( product => <CartItem key={product.id} 
                id={product.id} name={product.title} image={product.imagen} price={product.price} 
                amount={product.cantidad}/>)} 

                { cart.length > 0 &&
                <>
                <h2>${total}</h2>
                </>
                }


            </div>
        </>
    )
}

export default Carrito
